module.exports = function (source) {
    // 获取loader的配置
    const options = getOptions(this)
    // 校验配置：成功时往下运行，失败时停止运行报出错误
    validate(babelSchema, options, {
        name: 'babelLoader'
    })
    // 异步返回结果
